operation.add:
// OCFilter CHPU URL processing
if ($this->config->get('ocfilter_chpu_status')) {
    try {
        $this->load->library('ocfilter_chpu');
        if ($this->ocfilter_chpu && method_exists($this->ocfilter_chpu, 'processIncomingUrl')) {
            $this->ocfilter_chpu->processIncomingUrl();
        }
    } catch (Exception $e) {
        error_log('OCFilterCHPU error: ' . $e->getMessage());
    }
}

// OCFilter CHPU URL conversion for outgoing links
if ($this->config->get('ocfilter_chpu_status') && strpos($args, 'ocf=') !== false) {
    try {
        $this->load->library('ocfilter_chpu');
        if ($this->ocfilter_chpu && method_exists($this->ocfilter_chpu, 'convertUrlArgs')) {
            $args = $this->ocfilter_chpu->convertUrlArgs($route, $args);
        }
    } catch (Exception $e) {
        error_log('OCFilterCHPU URL conversion error: ' . $e->getMessage());
    }
}
